import React from "react";
import {
    View,
    Text,
    StyleSheet,
    FlatList,
    Image
} from 'react-native'
import HeaderDetail from "../components/HeaderDetail";

function _getStatus(status) {
    return status.trim() === "Completed" ? "green" :
        (status.trim() === "Pending Payment" ? "orange" :
            (status.trim() === "Processing" ? "blue" :
                (status.trim() === "On hold" ? "red" : "green")))
}

const OrderDetail = (props) => {
    const { navigation, route } = props
    const { navigate, goBack } = navigation

    const item = props.route.params

    return (
        <View style={styles.container}>
            <HeaderDetail
                name='arrow-left'
                title={`Order #0${item.id}`}
                onPressLeft={() => {
                    goBack()
                }}
            />
            <View style={{
                padding: 10
            }}>
                <View
                    style={{
                        flexDirection: "row",
                    }}>
                    <View style={{
                        marginEnd: 20
                    }}>
                        <Image source={require('../assets/images/short.jpg')} style={styles.imgProduct} />
                    </View>

                    <View style={{
                        flex: 1,
                    }}>
                        <View style={{
                            flexDirection: "row"
                        }}>
                            <Text style={{
                                fontWeight: "bold",
                                fontSize: 20,
                            }}>#0{item.id}</Text>

                            <View style={{ flex: 1 }} />

                            <View style={{
                                backgroundColor: _getStatus(item.status),
                                paddingHorizontal: 10,
                                paddingVertical: 5,
                                borderRadius: 100,
                            }}>
                                <Text style={{


                                    color: "white"
                                }}>{item.status}</Text>
                            </View>

                        </View>



                    </View>

                </View>
                <View style={{
                    flexDirection: "row",
                    marginTop: 10
                }}>
                    <Text style={{
                        fontSize: 16
                    }}>{item.date}   {item.time} AM   by {item.name}</Text>
                </View>
            </View>

            <FlatList

            />
        </View>
    )
}
export default OrderDetail

const styles = StyleSheet.create({
    container: {
        flex: 1
    },
    imgProduct: {
        width: 30,
        height: 30,
    },
    txtName: {
        fontSize: 24,
        fontWeight: "bold",
        marginBottom: 10
    }
})
