import React from "react";
import {
    View,
    Text,
    TouchableOpacity,
    Image,
    StyleSheet
} from 'react-native'

function _getStatus(status) {
    return status.trim() === "Completed" ? "green" :
        (status.trim() === "Pending Payment" ? "orange" :
            (status.trim() === "Processing" ? "blue" :
                (status.trim() === "On hold" ? "red" : "green")))
}

const OrderItem = (props) => {
    const { id, name, date, time, status, type } = props.item
    const {onPress} = props
    return (
        <TouchableOpacity
            onPress={onPress}
            style={{
                flexDirection: "row",
                marginVertical: 10,
                paddingVertical: 10
            }}>
            <View style={{
                marginEnd: 20
            }}>
                <Image source={require('../../assets/images/short.jpg')} style={styles.imgProduct} />
            </View>

            <View style={{
                flex: 1,
            }}>
                <View style={{
                    flexDirection: "row"
                }}>
                    <Text style={{
                        fontWeight: "bold",
                        fontSize: 20,
                    }}>#0{id}</Text>

                    <View style={{ flex: 1 }} />

                    <View style={{
                        backgroundColor: _getStatus(status),
                        paddingHorizontal: 10,
                        paddingVertical: 5,
                        borderRadius: 100,
                    }}>
                        <Text style={{


                            color: "white"
                        }}>{status}</Text>
                    </View>

                </View>

                <View style={{
                    flexDirection: "row",
                    marginTop: 10
                }}>
                    <Text style={{
                        fontSize:16
                    }}>{date}   {time} AM   by {name}</Text>
                </View>
                <Text
                    style={{
                        marginTop: 10,
                        fontSize: 18
                    }}
                    numberOfLines={1}                
                >{type}</Text>

            </View>

        </TouchableOpacity>
    )
}
export default OrderItem

const styles = StyleSheet.create({
    imgProduct: {
        width: 30,
        height: 30,
    },
    txtName: {
        fontSize: 24,
        fontWeight: "bold",
        marginBottom: 10
    }

})
