import React, {useState} from 'react'
import {
    View,
    Text,
    TextInput,
    StyleSheet,
    Image,
    FlatList,
    TouchableOpacity
} from 'react-native'
import { Colors } from '../components/Colors'
import Icon from 'react-native-vector-icons/FontAwesome'
import Header from '../components/Header'
import OrderItem from './order/OrderItem'
const OrderScreen = ({navigation}) =>{
    const {navigate, goBack} = navigation
    const [order, setOrder] = useState([
        {
            id : 1,
            name: "Hai",
            date: "04/04/2022",
            time: "10:00",
            status: "Completed",
            type: "Coca Cola x1, Pepsi x2, Redbull x3, one one sdfsfsdf"

        },
        {
            id : 2,
            name: "Hai",
            date: "04/04/2022",
            time: "10:00",
            status: "Pending Payment",
            type: "Coca Cola x1, Pepsi x2, Redbull x3, one one x3"

        },
        {
            id : 3,
            name: "Hai",
            date: "04/04/2022",
            time: "10:00",
            status: "Processing" ,
            type: "Coca Cola x1, Pepsi x2, Redbull x3, one one"

        },
        {
            id : 4,
            name: "Hai",
            date: "04/04/2022",
            time: "10:00",
            status: "On hold",
            type: "Coca Cola x1, Pepsi x2, Redbull x3, one one"

        },
    ])

    return(
        <View style={{flex: 1, padding: 10}}>    
            <Header 
                title = "Orders"
                image={require('../assets/svg/menu.png')}
            />
            <FlatList
                data={order}
                keyExtractor={item => item.id}
                renderItem={({ item }) => <OrderItem item = {item} onPress={()=>{
                    navigate('OrderDetail', item)
                }}/>
                }
            />
        </View>
    )
}
export default OrderScreen
