import React from 'react'
import {
    View,
    Text,
    TouchableOpacity,
    Image,
    FlatList
} from 'react-native'

import { SIZES, icons } from "../../constants"
const RenderFeature = () => {
    const featuresData = [
        {
            id: 1,
            icon: icons.pk,
            //color: COLORS.purple,
            //backgroundColor: COLORS.lightpurple,
            description: "Tạo đơn"
        },
        {
            id: 2,
            icon: icons.box,
            //color: COLORS.yellow,
            //backgroundColor: COLORS.lightyellow,
            description: "Sản phẩm"
        },
        {
            id: 3,
            icon: icons.carton,
            //color: COLORS.primary,
            //backgroundColor: COLORS.lightGreen,
            description: "Đơn hàng"
        },
        {
            id: 4,
            icon: icons.boxes,
            //color: COLORS.red,
            //backgroundColor: COLORS.lightRed,
            description: "Kho Hàng"
        },
        {
            id: 5,
            icon: icons.growth,
            //color: COLORS.yellow,
           // backgroundColor: COLORS.lightyellow,
            description: "Báo cáo"
        },
        {
            id: 6,
            icon: icons.customer,
            //color: COLORS.primary,
            //backgroundColor: COLORS.lightGreen,
            description: "Khách hàng"
        },
        {
            id: 7,
            icon: icons.tag,
            //color: COLORS.red,
            //backgroundColor: COLORS.lightRed,
            description: "Khuyến mãi"
        },
        {
            id: 8,
            icon: icons.gift,
            //color: COLORS.purple,
            //backgroundColor: COLORS.lightpurple,
            description: "Quà Tặng"
        },
    ]
    const [features, setFeatures] = React.useState(featuresData)

    const renderItem = ({ item }) => (
        <TouchableOpacity
            style={{
                marginBottom: SIZES.padding,
                alignItems: 'center',
            }}
            onPress={() => console.log(item.description)}
        >
            <View
                style={{
                    height: 100,
                    width: 90,
                    //marginBottom: 5,
                    borderRadius: 15,
                    backgroundColor: "white",
                    alignItems: 'center',
                    justifyContent: 'center',
                    
                }}
            >
                <Image
                    source={item.icon}
                    resizeMode="contain"
                    style={{
                        height: 30,
                        width: 30,
                        tintColor: item.color,
                        margin: 10
                    }}
                />
                <Text style={{ textAlign: 'center', flexWrap: 'wrap', fontSize: 12}}>{item.description}</Text>
            </View>
            
            {/* <Text style={{ textAlign: 'center', flexWrap: 'wrap', ...FONTS.body4 }}>{item.description}</Text> */}
        </TouchableOpacity>
    )
    return (
        <FlatList
            data={features}
            numColumns={4}
            columnWrapperStyle={{ justifyContent: 'space-between' }}
            keyExtractor={item => `${item.id}`}
            renderItem={renderItem}
            style={{ marginTop: SIZES.padding * 2 }}
        />
    )
}
export default RenderFeature
