import React, {useState} from 'react'
import {
    View,
    Text,
    TextInput,
    StyleSheet,
    Image,
    FlatList,
    TouchableOpacity
} from 'react-native'
import { Colors } from '../components/Colors'
import Icon from 'react-native-vector-icons/FontAwesome'
import Header from '../components/Header'
const ProductScreen = (props) => {
    const {navigation, route} = props
    const {navigate, goBack} = navigation
    const [product, setProduct] = useState([
        {
            id : 0,
            img: require('../assets/images/short.jpg'),
            name: "Jean",
            price: 120000,

        },
        {
            id : 1,
            img: require('../assets/images/short.jpg'),
            name: "Jean 1",
            price: 120000,

        },
        {
            id : 2,
            img: require('../assets/images/short.jpg'),
            name: "Jean 2",
            price: 120000,

        },
        {
            id : 3,
            img: require('../assets/images/short.jpg'),
            name: "Jean 3",
            price: 120000,

        },
        {
            id : 4,
            img: require('../assets/images/short.jpg'),
            name: "Jean 4",
            price: 120000,

        },
        {
            id : 5,
            img: require('../assets/images/short.jpg'),
            name: "Jean 5",
            price: 120000,

        },
    ])
    return (
        <View style={{ flex: 1, padding: 10 }}>           
            <Header 
                title = "All Product"
                image = {require('../assets/svg/menu.png')}
            />           
            <FlatList 
                data={product}
                keyExtractor={item => item.id}
                renderItem={({ item }) => (
                    <View style={{
                        flexDirection: "row",
                        alignItems: "center",
                        
                    }}>
                        <Image source={item.img} style={styles.imgProduct}/>
                        <View style={{
                            paddingHorizontal: 10
                        }}>
                            <Text style={styles.txtName}>{item.name}</Text>
                            <Text>{item.price} VND</Text>
                        </View>

                    </View>
                )}
            />
            <TouchableOpacity
                onPress={()=>{
                    navigate('FormProductScreen')
                }}
                style={{
                    backgroundColor: Colors.primary,
                    width: 50,
                    height: 50,
                    borderRadius: 25,
                    justifyContent: "center",
                    alignItems: "center",
                    position: "absolute",
                    bottom: 25,
                    right: 25
                }}>
                <Icon name='plus' size={20} color="white" />
            </TouchableOpacity>
        </View>

    )
}
export default ProductScreen
const styles = StyleSheet.create({
    imgProduct:{
        width: 150,
        height: 150,       
    },
    txtName:{
        fontSize: 24,
        fontWeight: "bold",
        marginBottom: 10
    }

})
