import React, { useState } from "react";
import {
    View,
    Text,
    StyleSheet,
    TextInput,
    TouchableOpacity
} from 'react-native'
import AsyncStorage from '@react-native-async-storage/async-storage';
const Login = (props) => {
    const { navigation, route } = props
    const { navigate, goBack } = navigation

    const [userName, setUserName] = useState('hai')
    const [passWord, setPassWord] = useState('pass')
    const [account, setAccount] = useState()

    const storeData = async (value) => {
        try {
          const jsonValue = JSON.stringify(value)
          await AsyncStorage.setItem('user', jsonValue)
        } catch (e) {
          console.log(e);
        }
      }
    const Login = () => {
        navigate('UiTab', { username: userName, password: passWord })
        // account.forEach(async (item) => {
        //     if (item.userName === userName && item.passWord === passWord) {
        //         await storeData(item)
                
        //     }
        //     else{
                
        //     }
        // });
        
    }
    return (
        <View style={styles.container}>
            <View style={{
                flex: 2,
                alignItems: 'center',
                justifyContent: 'center',
            }}>
                <Text style={styles.title}>Đăng Nhập</Text>
            </View>
            <View style={{
                flex: 3,
                width: "100%",
                alignItems: 'center',

            }}>
                <TextInput
                    style={styles.input}
                    placeholder="user"
                    onChangeText={(text) => {
                        setUserName(text)
                    }}
                    value={userName}

                />
                <TextInput
                    style={styles.input}
                    placeholder="pass"
                    onChangeText={(text) => {
                        setPassWord(text)
                    }}
                    value={passWord}
                    secureTextEntry={true}
                />

                <TouchableOpacity style={styles.btnLogin}
                    onPress={() => { Login() }}>
                    <Text>Login</Text>
                </TouchableOpacity>
            </View>
        </View>
    )
}
export default Login

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    title: {
        fontSize: 18,
        fontWeight: "bold",
    },
    input: {
        height: 40,
        margin: 12,
        borderWidth: 1,
        padding: 10,
        width: "80%"
    },
    btnLogin: {
        borderWidth: 1,
        width: "30%",
        height: 40,
        justifyContent: "center",
        alignItems: "center",
        margin: 12,
        borderRadius: 10
    }
})
