import React from 'react';
import {createBottomTabNavigator} from '@react-navigation/bottom-tabs'
import { HomeScreen, OrderScreen, ChatScreen, AccountStack, ProductScreen } from '../screens/index';
import Icon from 'react-native-vector-icons/FontAwesome'
import { Image } from 'react-native';
const Tab = createBottomTabNavigator()
const options = ({ route }) => ({
  headerShown: false,
  tabBarIcon: (focused, color, size) => {
    return (
      <Image source={route.name === "HomeScreen" ? require("../assets/svg/house.png") :
        (route.name === "ProductScreen" ? require("../assets/svg/package.png") :
          (route.name === "OrderScreen" ? require("../assets/svg/cargo.png") :
            (route.name === "ChatScreen" ? require("../assets/svg/chat.png") : 
        (route.name === "AccountStack" ? require("../assets/svg/user.png") : ""))))}
        style={{
          height: 30,
          width: 30
        }}
        resizeMode="contain"
      />
    )
  }
})
const tabOptions = {
  showLabel: false,
  style: {
      position: 'absolute',
      bottom: 0,
      left: 0,
      right: 0,
      backgroundColor: "transparent",
      elevation: 0
  }
};
const UiTab = () => {
  return (
    <Tab.Navigator
      tabOptions={tabOptions}
      screenOptions={options}
      initialRouteName='HomeScreen'
      >
       
      <Tab.Screen name='HomeScreen' component={HomeScreen}
        options={{
          tabBarLabel: "Home"
        }} />
      <Tab.Screen name='ProductScreen' component={ProductScreen}
        options={{
          tabBarLabel: "Product"
        }} />
      <Tab.Screen name='OrderScreen' component={OrderScreen}
        options={{
          tabBarLabel: "Order"
        }} />
      <Tab.Screen name='ChatScreen' component={ChatScreen}
        options={{
          tabBarLabel: "Chat"
        }} />
      <Tab.Screen name='AccountStack' component={AccountStack}
        options={{
          tabBarLabel: "Account"
        }} />
    </Tab.Navigator>
  );
}
export default UiTab
