import React, { useState } from "react";
import {
    View,
    Text,
    TextInput,
    ScrollView,
    Switch,
    StyleSheet,
    KeyboardAvoidingView,
    Image,
    TouchableOpacity,

} from 'react-native'
import { Colors } from "../components/Colors";
import HeaderDetail from "../components/HeaderDetail";
import RadioButton from "radio-buttons-react-native"
const FormProductScreen = ({ navigation }) => {
    const { navigate, goBack } = navigation
    const [multiline, setMultiline] = useState()
    const data = [
        {
            label: 'Shop only'
        },
        {
            label: 'Search results only'
        },
        {
            label: 'hidden'
        }
    ];
    return (
        <View style={styles.Container}>
            <HeaderDetail
                name="arrow-left"
                title="Create New Product"
                onPressLeft={() => { goBack() }}
            />
            <KeyboardAvoidingView behavior="height" style={styles.keyboard}>
                <ScrollView showsVerticalScrollIndicator={false}>
                    <View style={styles.content}>
                        <View style={styles.viewInput}>
                            <View style={styles.rowinput}>
                                <Text style={styles.txt}>Name</Text>
                                <TextInput
                                    style={styles.input}
                                />
                            </View>

                            <View style={styles.rowInput}>
                                <View style={styles.colInput}>
                                    <Text style={styles.txt}>Regular price</Text>
                                    <TextInput
                                        style={styles.input}
                                        keyboardType="number-pad"
                                    />
                                </View>
                                <View style={styles.colInput}>
                                    <Text style={styles.txt}>Sale Price</Text>
                                    <TextInput
                                        style={styles.input}
                                        keyboardType="number-pad"
                                    />
                                </View>
                            </View>

                            <View style={styles.rowInput}>
                                <View style={styles.colInput}>
                                    <Text style={styles.txt}>SKU</Text>
                                    <TextInput
                                        style={styles.input}
                                    />
                                </View>
                                <View style={styles.colInput}>
                                    <Text style={styles.txt}>Stock Quantity</Text>
                                    <TextInput
                                        style={styles.input}
                                    />
                                </View>
                            </View>

                            <View style={styles.rowInput}>
                                <View style={styles.colInput}>
                                    <View style={styles.viewManager}>
                                        <Text style={styles.txt}>Manage Stock</Text>
                                        <View style={{ flex: 1 }} />
                                        <Switch />
                                    </View>

                                </View>
                            </View>

                            <View style={styles.rowinput}>
                                <Text style={styles.txt}>Description</Text>
                                <TextInput
                                    style={styles.inputdes}
                                    editable={true}
                                    //maxLength={40}
                                    multiline={true}
                                    numberOfLines={10}
                                />
                            </View>
                            <View style={styles.rowInput}>
                                <View style={styles.colInput}>
                                    <Text style={styles.txt}>Feature Image</Text>
                                    <View style={{
                                        flex: 1,
                                        flexDirection: "row",
                                        marginTop: 10,
                                        justifyContent: "space-between",
                                        alignItems: "flex-end"
                                    }} >
                                        <View style={{
                                            width: 100,
                                            height: 100,
                                            justifyContent: "center",
                                            alignItems: "center",
                                            backgroundColor: "#d1d6de",
                                            borderRadius: 10,

                                        }}>
                                            <Image style={styles.img} source={require('../assets/images/default_image.png')} />
                                        </View>

                                        <TouchableOpacity style={{

                                            width: "40%",
                                            height: 50,
                                            justifyContent: "center",
                                            alignItems: "center",
                                            borderRadius: 25,
                                            backgroundColor: "#d1d6de",
                                        }}>
                                            <Text>Select Image</Text>
                                        </TouchableOpacity>


                                    </View>

                                </View>
                            </View>
                        </View>

                    </View>
                    <View style={styles.below}>
                        <TouchableOpacity style={styles.btncreate}>
                            <Text>Create Product</Text>
                        </TouchableOpacity>
                        <View style={{
                            width: "80%",
                            height: 100,
                            marginTop: 10
                        }}>
                            <RadioButton
                                data={data}
                                selectedBtn={(e) => console.log(e)}
                            />
                        </View>
                    </View>

                </ScrollView>
            </KeyboardAvoidingView>

        </View>
    )
}
export default FormProductScreen

const styles = StyleSheet.create({
    Container: {
        flex: 1
    },
    keyboard: {
        flex: 1
    },
    content: {
        paddingHorizontal: 25,
        alignItems: 'center',
    },
    viewInput: {
        width: "100%"
    },
    txt: {
        fontSize: 18
    },
    input: {
        height: 50,
        borderWidth: 1,
        borderColor: "#b0aea9",
        borderRadius: 10,
        marginTop: 10,
        padding: 10,
        backgroundColor: "white"
    },
    inputdes: {
        borderWidth: 1,
        height: 200,
        borderRadius: 10,
        padding: 10,
        borderColor: "#b0aea9",
        backgroundColor: "white",
        marginTop: 10,

    },
    rowInput: {
        flexDirection: 'row',
        marginHorizontal: -6,
        marginVertical: 10
    },
    colInput: {
        flex: 1,
        marginHorizontal: 6,
    },
    viewManager: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
    },
    img: {
        height: 50,
        width: 50,
    },
    btncreate: {
        width: "50%",
        height: 60,
        backgroundColor: Colors.primary,
        borderRadius: 25,
        justifyContent: "center",
        alignItems: "center",
        marginTop: 10
    },
    below: {
        marginTop: 10,
        backgroundColor: "white",
        width: "100%",
        alignItems: "center",
        height: 300
    }
})
