import endpoints from "./endpoints"

const base_url = "https://6239d0f528bcd99f02756145.mockapi.io"

export const Apis = {
    getInfo: async () => {
        const res = await fetch(`${base_url + endpoints.v1.getinfo()}`)
            .then((response) => { return response.json() })
            .catch((err) => {
                console.error(err)
            })
        return res
    }
}
