import React from "react";
import {
    View,
    Text,
    TouchableOpacity,
    StyleSheet,
    Image
} from 'react-native'
import { Colors } from "../../components/Colors";
const Report = () => {
    return(
        <View style={styles.container}>
            <View style={{
                flexDirection: "row",
                borderBottomWidth: 1,
                borderColor: "#f5f5f5"
            }}>
                <TouchableOpacity style={{
                    alignItems: "center",
                    width: "50%"
                }}>
                    <Text style={styles.txt}>Doanh thu hôm nay</Text>
                    <Text style={styles.number}>0 đ</Text>
                </TouchableOpacity>
                <View style={{borderWidth: 1, borderColor: "#f5f5f5"}}/>
                <TouchableOpacity style={{
                    alignItems: "center",
                    width: "50%"
                }}>
                    <Text style={styles.txt}>Lợi nhuận</Text>
                    <Text style={styles.number}>0 đ</Text>
                </TouchableOpacity>
            </View>
            <TouchableOpacity style={styles.btn}>
                <Image style={styles.img} source={require("../../assets/svg/profit.png")}/>
                <Text>Báo cáo lãi lỗ</Text>
            </TouchableOpacity>
        </View>
    )
}
export default Report
const styles = StyleSheet.create({
    container: {
        height: 150,
        borderRadius: 10,
        justifyContent: "center",
        alignItems: "center",
        borderRadius: 20,
        backgroundColor: "#fff"
    },
    txt:{
        padding: 5
    },
    btn:{
        marginTop: 10,
        padding: 10,
        flexDirection: "row",
        alignItems: "center"
    },
    img: {
        height: 25,
        width: 25,
        marginEnd: 10
    },
    number: {
        padding: 5,
        color: Colors.primary
    }
})
